        if ((ColpButtonGrp = QuickMenu?.transform.Find("CanvasGroup/Container/Window/QMParent/Menu_QM_GeneralSettings/Panel_QM_ScrollRect/Viewport/VerticalLayoutGroup/YourAvatar")?.gameObject) == null)
